import React, { useEffect, useState } from 'react';
import "./Navigation.scss";
import NavigationBackground from "./NavigationBackground";
import { Link } from 'react-router-dom';

export default function Navigation( {navOpen, setNavOpen} ) {

  const [renderedNavItems, setRenderedNavItems] = useState([]);

  useEffect(() => {
    for (let i = 1; i < navItems.length; i++) {
        setRenderedNavItems((prevState) => [...prevState, navItems[i]]);
    }
    /*
    for (let i = 1; i < navItems.length; i++) {
      setTimeout(() => 
        setRenderedNavItems((prevState) => [...prevState, navItems[i]]), 
        200 * i);
    }
    */
    for (let i = 1; i < navItems.length; i++) {
      setTimeout(() => 
        setRenderedNavItems((prevState) => [...prevState, navItems[i]]), 
        200 * i);
    }
  }, []);

  const handleClick = () => {
    setNavOpen(!navOpen);
  };

  const navItems = [
    {link : "/", label : "Home"},
    {link : "/about", label : "About" },
    {link : "/portfolio", label : "Portfolio" },
    {link : "/pricing", label : "Pricing" },
    {link : "/articles", label : "Articles" },
    {link : "/contact", label : "Contact" },
  ]

  return (
    <div>
      <NavigationBackground />
      <div className="navigation-container">
        <nav>
          <ul className="menu">
              {renderedNavItems.map((navItem, index) => (
                <li key={index}><Link to={navItem.link} onClick={handleClick}>{navItem.label}</Link></li>
              ))}
          </ul>
        </nav>
      </div>
    </div>
  )
}
