import { Link } from "react-router-dom";
import "./PreviewSection.scss";

export default function PreviewSection( {children, title, linkText, previewImage, layoutType} ) {
  var image = {  
    backgroundImage: `url(${process.env.PUBLIC_URL + "/assets/images/" + previewImage})`,
  };

  return (
    <section className="preview-section">
        {layoutType === "right" &&
          <div className="preview-image" style={image}/>
        }

        <div className="preview-description">
            <h4 className="preview-title title-1">
              {title}
            </h4>
            <div className="preview-excerpt">
                {children}
            </div>
            <Link to="/about-me">{linkText} {">"}</Link>
        </div>

        {layoutType === "left" &&
          <div className="preview-image" style={image}/>
        }
    </section>
  );
}
